mapper.select:
select postNo ,p.cateNo ,postTitle ,to_char(p.regDate, 'YYYY/MM/DD') regDate from category c, post p where c.cateNo = p.cateNo
and c.cateNo = #{cateNo}
order by regDate desc

select postNo ,postTitle ,postContent ,to_char(regDate, 'YYYY/MM/DD') regDate from post where regDate = (select max(p.regDate) from post p, category c where c.cateNo = p.cateNo
and c.cateNo = #{cateNo}
)
